package org.usfirst.frc1701.RobotBuild2015.commands;


import org.usfirst.frc1701.RobotBuild2015.Robot;
import org.usfirst.frc1701.RobotBuild2015.RobotMap;

import edu.wpi.first.wpilibj.Timer;
import edu.wpi.first.wpilibj.command.Command;

/**
 *
 */
public class  ElevatortoBottom extends Command {
	
	boolean finished = false;
	Timer timer = new Timer();
	
    public ElevatortoBottom() {
        // Use requires() here to declare subsystem dependencies
        // eg. requires(chassis);

        // BEGIN AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=REQUIRES
        requires(Robot.elevator);

    // END AUTOGENERATED CODE, SOURCE=ROBOTBUILDER ID=REQUIRES
    }

    // Called just before this Command runs the first time
    protected void initialize() {
    }

    // Called repeatedly when this Command is scheduled to run
    protected void execute() {
    	finished = false;
    	if(!RobotMap.elevatorStepLimitSwitch.get()){
    		timer.start();
    	}
    	if(!RobotMap.elevatorBottomLimitSwitch.get()){
    		RobotMap.elevatorElevatorMotorL.set(0.8);
    		RobotMap.elevatorElevatorMotorR.set(0.8);
    	}else{
    		finished = true;
    	}
    	if(Math.abs(Robot.oi.getElevatorJoystick().getY()) > 0.15){
    		finished = true;
    	}
    	if(timer.get() > 0.2){
    		finished = true;
    	}
    }

    // Make this return true when this Command no longer needs to run execute()
    protected boolean isFinished() {
        return finished;
    }

    // Called once after isFinished returns true
    protected void end() {
    	RobotMap.elevatorElevatorMotorL.set(0);
		RobotMap.elevatorElevatorMotorR.set(0);
		RobotMap.elevatorHeight = 0;
    }

    // Called when another command which requires one or more of the same
    // subsystems is scheduled to run
    protected void interrupted() {
    	finished = true;
    }
}
